	//Input Parameter 	: EventCreation_Txt_DateOfEvent, RMAApp_EventCreation_Txt_DateReported, RMAApp_EventCreation_Txt_DateReported,RMAApp_EventCreation_Txt_EventStatus, RMAApp_EventCreation_Txt_EventType, RMAApp_EventCreation_Txt_TimeReported,RMAApp_EventCreation_Txt_TimeOfEvent, EventCreation_Txt_Department Of Type String; Intlogval Of Type Integer
