	// ==========================================================================================================================================================================================================================================================
	public static String RMA_SendMail_Utility(String RMAApp_SendMail_Txt_To, String RMAApp_SendMail_Txt_CC, String ObjDesc, String SelectType, String Entity, boolean SendMailCall, int Intlogval) throws Exception, Error
	{
		try{
			String CancelMailBody;
			//Variable Declaration Completed Here
			ReturnValue = null;
			//Variable Initialization Completed Here
			if (Intlogval == 0)
			{
				parentlogger.log(LogStatus.INFO,"Verify That Mail Is Sent Successfully From " + " "+ ObjDesc);	
			}
			else
			{
				logger = reports.startTest("<span id='SendMail'>Send Mail Functionality</span>", "Verify That Mail Is Sent Successfully From " + " "+ ObjDesc);
			}
			ReturnValue = RMA_GenericUsages_Utility.RMA_RandomStringGeneration_Utility(4, "MailBody_");
			RMA_GenericUsages_Utility.RMA_DynamicFrameSwitching(RMA_Selenium_POM_VerificationMessage.SendMailFrame);
			RMA_GenericUsages_Utility.RMA_StaticWait(6, Intlogval, "Wait Is Added As Frame Is Getting Loaded");
			RMA_Verification_Utility.RMA_PartialTextVerification("New Memo",RMA_Selenium_POM_SendMail.RMAApp_SendMail_Header(driver).getText(), "Correct Send Mail Window Header",Intlogval);
			RMA_Input_Utility.RMA_SetTextValueandTabOut_Utility(RMA_Selenium_POM_SendMail.RMAApp_SendMail_Txt_To(driver), "To TextBox On Send Mail Page",RMAApp_SendMail_Txt_To,Intlogval);	
			RMA_Input_Utility.RMA_SetTextValueandTabOut_Utility(RMA_Selenium_POM_SendMail.RMAApp_SendMail_Txt_CC(driver), "CC TextBox On Send Mail Page",RMAApp_SendMail_Txt_CC,Intlogval);	
			RMA_Input_Utility.RMA_SetValue_Utility(RMA_Selenium_POM_SendMail.RMAApp_SendMail_Txt_Body(driver), "Body TextBox On Send Mail Page",ReturnValue,Intlogval);	
			//Send Mail Frame Is Switched, To And CC, Mail Body TextBoxes Are Provided Values

			//Thread.sleep(5000);
			RMA_Navigation_Utility.RMA_WebCheckBoxSelect_Utility(RMA_Selenium_POM_SendMail.RMAApp_SendMail_Chk_AttachToDocument(driver), "check","Attach To Document CheckBox", "Send Mail Page",Intlogval);	
			//Thread.sleep(5000);
			RMA_Navigation_Utility.RMA_WebCheckBoxSelect_Utility(RMA_Selenium_POM_SendMail.RMAApp_SendMail_Chk_ExecuiveSummary(driver), "check","Attach Executive Summary Report", "Send Mail Page",Intlogval);	
			RMA_Input_Utility.RMA_ElementWebListSelect_Utility(RMA_Selenium_POM_SendMail.RMAApp_SendMail_Lst_SelectType(driver), SelectType, "Select Type List Box", "Send Mail Page", Intlogval);
			// AttachToDocument, Executive Summary CheckBoxes Are Checked And SelectType Web List Is Provided Values

			RMA_Verification_Utility.RMA_PartialTextVerification(Entity,RMA_Selenium_POM_SendMail.RMAApp_SendMail_Txt_Regarding(driver).getAttribute("value"), "Correct Regarding Text For Send Mail",Intlogval);
			RMA_Verification_Utility.RMA_PartialTextVerification(Entity,RMA_Selenium_POM_SendMail.RMAApp_SendMail_Txt_Subject(driver).getAttribute("value"), "Correct Subject Text For Send Mail",Intlogval);	
			RMA_Navigation_Utility.RMA_ObjectJavaScriptClick(RMA_Selenium_POM_SendMail.RMAApp_SendMail_Btn_Send(driver), "Send Button On Send Mail Page",Intlogval);
			RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(Intlogval); // Wait For UX Page Load Bar To Disappear
			if (Intlogval == 0)
			{
				parentlogger.log(LogStatus.INFO, logger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver,"Send Mail Successful", "Send Mail Is Done Successfully")));
			}else{	
				logger.log(LogStatus.INFO, logger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver, "Send Mail Successful","Send Mail Is Done Successfully")));
			}
			//Regarding And Subject Field Values Are Checked And Mail Is Sent

			RMA_Verification_Utility.RMA_ElementExist_Utility(RMA_Selenium_POM_Home.RMAApp_Generic_RMAAppText(driver, RMA_Selenium_POM_VerificationMessage.EmailSuccessfulSend), "Successful Email Sent Toast Message", Intlogval);
			driver.switchTo().parentFrame();

			if (SendMailCall==true)
			{
				RMA_Navigation_Utility.RMA_ObjectJavaScriptClick(RMA_Selenium_POM_Home.RMAApp_Generic_Img_SendMail(driver), "Send Mail Image On "+ObjDesc, Intlogval);
			}
			else
			{
				RMA_Functionality_Utility.RMA_ListOptionClick_Utility("Claim-SendMail", Intlogval);
			}

			RMA_GenericUsages_Utility.RMA_DynamicFrameSwitching(RMA_Selenium_POM_VerificationMessage.SendMailFrame);
			RMA_GenericUsages_Utility.RMA_StaticWait(6, Intlogval, "Wait Is Added As Frame Is Getting Loaded");
			RMA_Input_Utility.RMA_SetTextValueandTabOut_Utility(RMA_Selenium_POM_SendMail.RMAApp_SendMail_Txt_To(driver), "To TextBox On Send Mail Page",RMAApp_SendMail_Txt_To,Intlogval);	
			CancelMailBody= RMA_GenericUsages_Utility.RMA_RandomStringGeneration_Utility(4, "MailBody_");
			RMA_Input_Utility.RMA_SetValue_Utility(RMA_Selenium_POM_SendMail.RMAApp_SendMail_Txt_Body(driver), "Body TextBox On Send Mail Page",CancelMailBody,Intlogval);	
			RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_SendMail.RMAApp_SendMail_Btn_Cancel(driver), "Cancel Button On Send Mail Page",Intlogval);
			//Send Mail Frame Is Switched, To , Mail Body TextBoxes Are Provided Values

			RMA_NegativeVerification_Utility.RMA_ElementNotExist_Utility((RMA_Selenium_POM_Home.RMAApp_Generic_RMAAppText(driver, RMA_Selenium_POM_VerificationMessage.EmailSuccessfulSend)), "Successful Email Sent Toast Message", Intlogval);

		}catch (Exception|Error e) {
			if (Intlogval == 0)
			{
				parentlogger.log(LogStatus.INFO, logger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver,"Send Mail Fail", "Send Mail Is Not Done")));
			}else{	
				logger.log(LogStatus.INFO, logger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver, "Send Mail Fail","Send Mail Is Not Done")));
			}

			if (!(RMA_Selenium_POM_Home.RMAApp_GenericToast_ErrorMsg(driver)== null))
			{
				ErrorTxtMessage = RMA_Selenium_POM_Home.RMAApp_GenericToast_ErrorMsg(driver).getText();
				if (Intlogval ==0)
				{
					parentlogger.log(LogStatus.FAIL, "UnExpected Error Toast Message With Following Text Is Displayed" + " "+ color.RMA_ChangeColor_Utility(ErrorTxtMessage,1));
				}
				else
				{
					logger.log(LogStatus.FAIL, "UnExpected Error Toast Message With Following Text Is Displayed" + " " +color.RMA_ChangeColor_Utility(ErrorTxtMessage,1));
				}
				RMA_Navigation_Utility.RMA_ObjectJavaScriptClick(RMA_Selenium_POM_Home.RMAApp_GenericBtn_ErrorToastMsgClose(driver), "Close Button On Error Toast Message", Intlogval);// Save Button Is Clicked
			}
			driver.switchTo().parentFrame();
			RMA_Navigation_Utility.RMA_ObjectJavaScriptClick(RMA_Selenium_POM_SendMail.RMAApp_SendMail_Btn_HeaderClose(driver), "Close Image On Send Mail Page Header (Clicked As A Clean Up Step)",Intlogval);	
			throw (e);
		}
		finally
		{
			if (!(RMA_Selenium_POM_SendMail.RMAApp_SendMail_Btn_Cancel(driver)== null)){
				if (Intlogval ==0)
				{
					parentlogger.log(LogStatus.FAIL, color.RMA_ChangeColor_Utility("Unexpected Error Occurred On Function RMA_SendMail_Utility",1));
				}
				else
				{
					logger.log(LogStatus.FAIL, color.RMA_ChangeColor_Utility("Unexpected Error Occurred On Function RMA_SendMail_Utility",1));
				}
				RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_SendMail.RMAApp_SendMail_Btn_Cancel(driver), "Cancel Image Button On Send Mail Screen (Finally Block Step)", Intlogval);		
				driver.switchTo().parentFrame();
				Assert.fail();		
			}
			else
			{
				driver.switchTo().parentFrame();
			}
		}
		return ReturnValue;
	}

	
	//FunctionName 		: RMA_StandardSearch_Utility
	//Description  		: Enables Users To Search Any Type Of Entity On The Opened Search Screen
	//Input Parameter 	: WebElement Element : Element In Which The Searched Text (String Type Parameter Is Passed), ControlDesc, ScreenDesc : Respectively Provide Description About The Search Screen (Like Claim Search) And The control Name In Which The Searched Text Is Entered, Link Text Of The Link To Be Clicked,CurrentFrame Of Type String Providing The Value/ID Of The Frame To Be Switched Once Search Is Done
	//					: Intlogval Is The Integer Value Of The Logger Statement Variable "logger" Or "ParentLogger"
	//Revision			: 0.0 - KumudNaithani-06-26-2017                             
	
	public static void RMA_StandardSearch_Utility(WebElement Element, String ScreenDesc, String ControlDesc, String SearchedText, String LinkText, String CurrentFrame, int Intlogval) throws Exception,Error
	{
		try{

			RMA_Input_Utility.RMA_SetValue_Utility(Element, ControlDesc+  " "+ "On" + ScreenDesc, SearchedText, Intlogval);
			RMA_Navigation_Utility.RMA_WebCheckBoxSelect_Utility(RMA_Selenium_POM_Search.RMAApp_Search_Chk_Soundex(driver), "uncheck", "Soundex CheckBox", ScreenDesc, Intlogval);
			RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_Search.RMAApp_Search_Btn_Submit(driver), "Submit Button On"+ " " + ScreenDesc, Intlogval);
			RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(Intlogval);
			RMA_GenericUsages_Utility.RMA_StaticWait(15, Intlogval, "Wait Is Added As The Searched Record Is Getting Loaded");
			CurrentFrameID=RMA_GenericUsages_Utility.RMA_GetCurrentFrameID();
			RMA_GenericUsages_Utility.RMA_SwitchNextFrameID(CurrentFrameID);
			RMA_Navigation_Utility.RMA_ObjectJavaScriptClick(RMA_Selenium_POM_Home.RMAApp_Generic_RMAAppText(driver, LinkText), ScreenDesc, Intlogval);
			//RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(Intlogval);
			driver.switchTo().parentFrame();
			RMA_GenericUsages_Utility.RMA_DynamicFrameSwitching(CurrentFrame);
			RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(Intlogval);
			RMA_GenericUsages_Utility.RMA_StaticWait(10, Intlogval, "Wait Is Added As Frame Is Getting Loaded");

		}catch(Exception|Error e)
		{
			if (Intlogval == 0)
			{
				parentlogger.log(LogStatus.INFO, logger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver, "Entity Search Fail", "Given Entity Is Not Searched")));
			}else{	
				logger.log(LogStatus.INFO, logger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver, "Entity Search Fail", "Given Entity Is Not Searched")));
			}
			throw (e);
		}	
	}
