			RMA_GenericUsages_Utility.RMA_DynamicFrameSwitching(RMA_Selenium_POM_VerificationMessage.LOBParameterFrame);					
			RMA_GenericUsages_Utility.RMA_StaticWait(6, Intlogval, "Wait Is Added As Frame Is Getting Loaded");
			//==============================================================Steps To Navigate To LOB Parameter Setup Screen Is Completed Here=================================================================================================================================================================================================================================================

			//==============================================================Steps To Select The Claim Options Tab On LOB Parameter Setup Screen Is Started Here=============================================================================================================================================================================================================================
			RMA_Input_Utility.RMA_ElementWebListSelect_Utility(RMA_Selenium_POM_LOBClaimOptions.RMAApp_LOBParaSetUp_Lst_LOB(driver), ClaimType, "Line of Business List Box", "LOBParameter Setup Page" ,Intlogval);		
			RMA_GenericUsages_Utility.RMA_StaticWait(5, Intlogval, "Wait Is Added As Claim Options Tab Needs To Be Clicked");
