				RMA_GenericUsages_Utility.RMA_StaticWait(5, Intlogval, "Wait Is Added As Adjuster LastName Value Is Provided To Adjuster LastName Text Field");
				RMA_GenericSave_Utility(CurrentFrameID, false, "NA", "Adjuster Creation Page", Intlogval);
