	public static void RMA_ErrorMessageOnSave (boolean VerifyErrorMessage,String ExpectedErrorMessage, int logval) throws Exception, Error {

		try {  

			boolean matchFound;

			Thread.sleep(3000);

			if(VerifyErrorMessage==true)

			{

				RMA_Verification_Utility.RMA_ElementExist_Utility(RMA_Selenium_POM_Home.RMAApp_GenericToast_ErrorMsg(driver), "RMA Application Error Message",logval);// Check Error Message On Screen  

				List<WebElement> lis = driver.findElements(By.xpath(".//*[@class='toast toast-error']"));

				String[] ErrorMsgLists=ExpectedErrorMessage.split("~");

				for (String ErrorMsg: ErrorMsgLists)
				{

					matchFound=false;

					for (WebElement li : lis) 

					{

						String ErrormessageActual=li.getText();

						//Match check

						if (ErrormessageActual.contains(ExpectedErrorMessage.trim()))
						{
							if (logval == 0)
							{
								parentlogger.log(LogStatus.PASS, "Expected Value of Error Message" + " "+ color.RMA_ChangeColor_Utility(ExpectedErrorMessage.trim(), 3) + " Is Contained In Actual Value of Error Message" + " " + color.RMA_ChangeColor_Utility(ErrormessageActual, 2) + " "  + "Hence Error Message Found Successful" );
							}
							else

							{
								logger.log(LogStatus.PASS, "Expected Value of Error Message" + " "+ color.RMA_ChangeColor_Utility(ExpectedErrorMessage.trim(), 3) + " Is Contained In Actual Valueof Error Message" + " " + color.RMA_ChangeColor_Utility(ErrormessageActual, 2) + " "  + "Hence Error Message Found Successful" );
							}
							matchFound=true;                                       
							break;
						}                                        
					}
					//Not matched        

					if(!matchFound)

					{                                                                    
						if (logval == 0)

						{
							parentlogger.log(LogStatus.FAIL, "Expected Value of Error Message" + " "+ color.RMA_ChangeColor_Utility(ExpectedErrorMessage.trim(), 1) + " Is Not Contained On RMA Application Error List, Hence Error Message Not Found!");

						}
						else
						{             
							logger.log(LogStatus.FAIL, "Expected Value of Error Message" + " "+ color.RMA_ChangeColor_Utility(ExpectedErrorMessage.trim(), 1) + " Is Not Contained On RMA Application Error List, Hence Error Message Not Found!");

						}
					}
				}
			}

			else

			{

				RMA_NegativeVerification_Utility.RMA_ElementNotExist_Utility(RMA_Selenium_POM_Home.RMAApp_GenericToast_ErrorMsg(driver), "RMA Application Error Message Heading",logval);

			}

		}

		catch (Exception|Error e) {

			//System.out.println("Error Occurred In Method ErrorMessage While Reading and Verifying The Error Message :" +" " +e.getMessage());

			throw(e);

		}

	}


