	//==========================================================================================================================================================================================================================================
	public static String RMA_VehicleEntityAddition_Utility(String Entity, String ReqParam, String ReqParam1,String VehicleRecord ,int logval) throws Exception, Error
	{		
		try {
			logger = reports.startTest("<span id='MaintVehNewEntity'>Vehicle Entity Addition</span>", "A New Entity Of Type" + " "+ Entity+" "+ "Is Added For Following VehicleID:: " + VehicleRecord + " From Policy Screen");			
			String FrameID;
			switch (Entity){				
			case "Inspection":	

				globalWait.until(ExpectedConditions.visibilityOf(RMA_Selenium_POM_Home.RMAApp_GenericBtn_EnityAdd(driver, "Inspections")));
				RMA_Navigation_Utility.RMA_ObjectJavaScriptClick(RMA_Selenium_POM_Home.RMAApp_GenericBtn_EnityAdd(driver, "Inspections"), "Add Image Button For Vehicle Inspection For Vehicle With ID:: " + VehicleRecord + " On Vehicle Creation Page",logval);					
				RMA_Input_Utility.RMA_SetValue_Utility(RMA_Selenium_POM_Vehicle.RMAApp_VehicleInsp_Txt_InspectionDate(driver), "Inspection Date TextBox On Vehicle Inspection Page", RMA_GenericUsages_Utility.RMA_CurrentDateInfo_Utility("MM/dd/yyyy"), logval);
				RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_Vehicle.RMAApp_VehicleInsp_Btn_InspectionResult(driver), "Inspection Result TextBox On Vehicle Inspection Page",logval);	
				RMA_GenericUsages_Utility.RMA_StaticWait(5, logval, "Wait Is Added As Without Wait The Text Added In Inspection Result Field Is Not Reflected");
				RMA_Input_Utility.RMA_SetValue_Utility(RMA_Selenium_POM_Vehicle.RMAApp_VehicleInspecResult_Txt_Content(driver), "Content TextBox On Vehicle Inspection-->Inspection Result Page", RMA_GenericUsages_Utility.RMA_RandomStringGeneration_Utility(4, "InspecResult_"), logval);
				RMA_GenericUsages_Utility.RMA_StaticWait(2, logval, "Wait Is Added As Without Wait The Text Added In Inspection Result Field Is Not Reflected");
				RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_Vehicle.RMAApp_VehicleInspecResult_Btn_OK(driver), "OK Image Button On Vehicle Inspection Page",logval);

				FrameID= RMA_GenericUsages_Utility.RMA_GetCurrentFrameID();					
				RMA_Functionality_Utility.RMA_GenericSave_Utility(FrameID, false, "NA",  "Vehicle-->Vehicle Creation Page", logval);
				ReturnValue =  RMA_Verification_Utility.RMA_AttributeFetch_Utility(RMA_Selenium_POM_Vehicle.RMAApp_VehicleInsp_Txt_InspectionDate(driver), "value");
				RMA_Verification_Utility.RMA_TextCompare(RMA_GenericUsages_Utility.RMA_CurrentDateInfo_Utility("MM/dd/yyyy"), ReturnValue, "Inspection Date Value", logval);

				if (logval == 0)
				{
					parentlogger.log(LogStatus.INFO, parentlogger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver, "Vehicle Inspection Saved Successfully", "Successful Vehicle Inspection Creation With Inspection Date " + ReturnValue )));
				}else{	
					logger.log(LogStatus.INFO, logger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver, "Vehicle Inspection Saved Successfully", "Successful Vehicle Inspection Creation With Inspection Date " + ReturnValue )));
				}
				break;	

			default:

				if (logval == 0)
				{
					parentlogger.log(LogStatus.ERROR, "Case Variable" + " "+Entity+ " " + "Provided By User Is Not Having A Defined Case Code Logic And Hence Is An Invalid Input");	
				}
				else
				{
					logger.log(LogStatus.ERROR, "Case Variable" + " "+ Entity+" " + "Provided By User Is Not Having A Defined Case Code Logic And Hence Is An Invalid Input");	
				}
			}
			return ReturnValue;

		} catch (Exception|Error e) {
			throw (e);
		}
	}


