	//FunctionName 		: RMA_ScheduleCheckCreation
	//Description  		: Enables Users To Create Schedule Check
	//Input Parameter 	: Input Parameters Are :PayInterval,NumberOfPayment, BankAccount, PayeeType, TransactionType, Amount, Distribution Type, logval 
	//Revision			: 0.0-NidhiJha-06-11-2017
	//Modified By		: 0.1-DebasmitaPati-08-26-2019 (Commented Finally Block For Third Party Payment Addition In Auto Check)
	//Modified By       : 0.2-AnubhutiVyas-06-08-2020 (Added One Parameter For Selecting Distribution Type)
	//======================================================================================================================================================================================================================================================
	public static String RMA_ScheduleCheckCreation(String PayInterval,int NumberOfPayment, String BankAccount, String PayeeType, String PayeeName, String TransactionType,int Amount,String DistributionType,int logval) throws Exception,Error
	{		
		CurrentFrameID=null;
		ReturnValue=null;
		//Global Variable Initialization Is Completed Here

		try
		{
			if (logval == 0)
			{
				parentlogger.log(LogStatus.INFO,"Schedule Check Is Created On Claim");
			}
			else
			{	
				logger = reports.startTest("<span id='ScheduleCheckOnClaim'>ScheduleCheck Creation On Claim</span>","Schedule Check Is Created On Carrier GC Claim");
			}

			CurrentFrameID=RMA_GenericUsages_Utility.RMA_GetCurrentFrameID();

			RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_ScheduleCheck.RMAApp_FinancialReserve_Img_ScheduleCheck(driver), "ScheduleCheck Image On Financial Reserve Page", logval);
			RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(logval);
			RMA_GenericUsages_Utility.RMA_StaticWait(6, logval, "Wait Is As ScheduleCheck Page Is Loaded");
			//Schedule Check Page Is Loaded

			RMA_Input_Utility.RMA_ActionSetValueAndTabOut_Utility(RMA_Selenium_POM_ScheduleCheck.RMAApp_ScheduleCheck_Txt_NumberOfPayments(driver), "# Of Payments Text Box On ScheduleCheck Page", String.valueOf(NumberOfPayment), logval);
			RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(logval);

			//Added by Anubhuti to Select Distribution Type
			RMA_Navigation_Utility.RMA_ObjectJavaScriptClick(RMA_Selenium_POM_PaymentsCollections.RMAApp_PaymentCollection_Btn_DistributionType(driver), "Distribution Type Code Look Up Button On RMA Application Payments Creation Page",logval);
			RMA_GenericUsages_Utility.RMA_StaticWait(5, logval, "Wait Added For Distribution Selection Window");
			RMA_Navigation_Utility.RMA_WebPartialLinkSelect(DistributionType, "Distribution Type Link On Distribution Selection Window", logval);
			RMA_GenericUsages_Utility.RMA_StaticWait(5, logval, "Wait Is Added As Distribution Type Is Selected In Payments Creation Page");
			//Added by Anubhuti to Select Distribution Type

			RMA_Input_Utility.RMA_SetTextValueandTabOut_Utility(RMA_Selenium_POM_ScheduleCheck.RMAApp_ScheduleCheck_Txt_PayInterval(driver), "Pay Interval Text Box On Schedule Check  Page", PayInterval, logval);
			RMA_GenericUsages_Utility.RMA_StaticWait(2, logval, "Wait Is Added As Value Is Entered In Pay Interval Text Box");
			RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(logval);
			RMA_Navigation_Utility.RMA_ObjectActionsClick(RMA_Selenium_POM_ScheduleCheck.RMAApp_ScheduleCheck_Txt_ControlNumber(driver), "Control Number Textbox On Schedule Check Page", logval);
			RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(logval);
			RMA_GenericUsages_Utility.RMA_StaticWait(12, logval, "Wait Is Added As Number Of Payments Will Get Reflected In The Detail Information Tab");
			//Number Of Payments Is Set		
			RMA_Input_Utility.RMA_ElementWebListSelect_Utility(RMA_Selenium_POM_ScheduleCheck.RMAApp_ScheduleCheck_Lst_BankAccount(driver), BankAccount, "BankAccount", "Schedule Check Page", logval); 
			//Bank Account Is Selected 
			RMA_Input_Utility.RMA_ElementWebListSelect_Utility(RMA_Selenium_POM_ScheduleCheck.RMAApp_ScheduleCheck_Lst_PayeeType(driver), PayeeType, "PayeeType", "Schedule Check Page", logval);
			//Payee Type Is Selected
			if(!"C-Claimant".equals(PayeeType.trim())){
				RMA_Input_Utility.RMA_ActionSetValueAndTabOut_Utility(RMA_Selenium_POM_PaymentsCollections.RMAApp_PaymentCollection_Txt_LastName(driver), "Last Name Text Box On Schedule Check Page",PayeeName,logval);
				RMA_GenericUsages_Utility.RMA_StaticWait(2, logval, "Wait Is Added As Value Is Entered In Last Name Box");
				RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(logval);
			}
			globalWait.until(ExpectedConditions.elementToBeClickable(RMA_Selenium_POM_ScheduleCheck.RMAApp_ScheduleCheck_Tab_DetailedInformation(driver)));
			RMA_Navigation_Utility.RMA_ObjectJavaScriptClick(RMA_Selenium_POM_ScheduleCheck.RMAApp_ScheduleCheck_Tab_DetailedInformation(driver), "Expand Detailed Information Tab On Schedule Check Page", logval);
			RMA_GenericUsages_Utility.RMA_StaticWait(2, logval, "Wait Is Added As Detailed Information Tab Is Clicked");
			//Detailed Information Tab Is Loaded

			RMA_Navigation_Utility.RMA_ScrollIntoView(RMA_Selenium_POM_ScheduleCheck.RMAApp_ScheduleCheck_Img_PaymentGridNew(driver), "New Image For Transaction On Schecule Check Page", logval);
			RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_ScheduleCheck.RMAApp_ScheduleCheck_Img_PaymentGridNew(driver), "New Image For Transaction On Schecule Check Page", logval);
			RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_ScheduleCheck.RMAApp_ScheduleCheck_Btn_TransType(driver), "Transaction Type Button On Schedule Check Page", logval);
			RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(logval);
			RMA_GenericUsages_Utility.RMA_StaticWait(2, logval, "Wait Is Added As Transaction Type Is Set");
			//RMA_Navigation_Utility.RMA_ObjectJavaScriptClick(driver.findElement(By.xpath(".//*[@class='ui-grid-cell-contents ng-scope']/span")), "Transaction Type On Lookup Window", logval);
			RMA_Navigation_Utility.RMA_WebPartialLinkSelect(TransactionType, "Transaction Type Link ", logval);
			RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(logval);
			RMA_GenericUsages_Utility.RMA_StaticWait(2, logval, "Wait Is Added As Transaction Type Is Set");

			RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_FinancialReserves.RMAApp_FundsSplitDetails_Txt_Amount(driver), "Amount TextBox On  Payment Detail Window", logval);
			RMA_Input_Utility.RMA_ActionSetValueAndTabOut_Utility(RMA_Selenium_POM_FinancialReserves.RMAApp_FundsSplitDetails_Txt_Amount(driver), "Amount TextBox On  Payment Detail Window", String.valueOf(Amount),logval);						
			RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_Home.RMAApp_Generic_Img_Save(driver), "Save Image On Payment Detail Entry Page", logval);
			RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(logval);
			RMA_GenericUsages_Utility.RMA_StaticWait(5, logval, "Wait Is Added As Payment Detail Is Saved");
			globalWait.until(ExpectedConditions.elementToBeClickable(RMA_Selenium_POM_Home.RMAApp_Generic_Img_Save(driver)));
			RMA_GenericSave_Utility(CurrentFrameID, false, "NA", "Schedule Check Page", logval);

			RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_ScheduleCheck.RMAApp_ScheduleCheck_Tab_ScheduleCheck(driver), "Schedule Check Tab On Schedule Check Page", logval);
			ReturnValue = RMA_Verification_Utility.RMA_AttributeFetch_Utility(RMA_Selenium_POM_ScheduleCheck.RMAApp_ScheduleCheck_Txt_ControlNumber(driver), "value");

			if(NumberOfPayment == 5)
			{
				RMA_Verification_Utility.RMA_TextCompare(RMA_GenericUsages_Utility.RMA_CurrentDateInfo_Utility("MM/dd/yyyy"), RMA_Selenium_POM_ScheduleCheck.RMAApp_ScheduleCheck_Txt_FirstPayment(driver).getAttribute("value"), "Fisrt Payment Date TextBox On Schedule Check Page", logval);
				RMA_Verification_Utility.RMA_TextCompare(RMA_GenericUsages_Utility.RMA_PastFutureDateInfo_Utility(+4,0,0,"MM/dd/yyyy"), RMA_Selenium_POM_ScheduleCheck.RMAApp_ScheduleCheck_Txt_FinalPayment(driver).getAttribute("value"), "Final Payment Date TextBox On Schedule Check Page", logval);
			}
			if(logval == 0)
			{
				parentlogger.log(LogStatus.PASS, "Schedule Check On Claim Created Successfully With Auto Control Number:" + " " + color.RMA_ChangeColor_Utility(ReturnValue,2));
			}
			else
			{
				logger.log(LogStatus.PASS, "ScheduleCheck Is  On Claim Created Successfully With Auto Control Number:" + " " + color.RMA_ChangeColor_Utility(ReturnValue,2));
			}
			return ReturnValue;
		}
		catch (Exception|Error e)
		{
			if (logval == 0)
			{
				parentlogger.log(LogStatus.INFO, logger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver, "Schedule Check On Carrier GC Claim Fail", "Schedule Check On Claim Is UnSuccessful")));
			}else{	
				logger.log(LogStatus.INFO, logger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver, "Schedule Check On Carrier GC Claim Fail", "Schedule Check On Claim Is UnSuccessful")));
			}

			throw (e);
		}
	}

