			RMA_Navigation_Utility.RMA_ObjectJavaScriptClick(RMA_Selenium_POM_Home.RMAApp_Generic_RMAAppText(driver, "Supplemental Field Data"), "Supplemental Field Data Link Text On Utilities Screen", Intlogval);
