			//RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(Intlogval);
			Thread.sleep(2000);
			globalWait.until(ExpectedConditions.presenceOfElementLocated(By.xpath("//*[@id='selectrdo']")));
