				RMA_Input_Utility.RMA_SetValue_Utility(RMA_Selenium_POM_Home.RMAApp_Generic_Txt_UXGrid_FilterTextBox(driver, "Control #"), "Control Number Filter Text Box On Transaction History Grid", StrControlNumber, Intlogval);
