			globalWait.until(ExpectedConditions.elementToBeClickable(RMA_Selenium_POM_Home.RMAApp_Generic_RMAAppText(driver, "Line Of Business Parameter Setup")));
			RMA_Navigation_Utility.RMA_ObjectJavaScriptClick(RMA_Selenium_POM_Home.RMAApp_Generic_RMAAppText(driver, "Line Of Business Parameter Setup"), "Line Of Business Parameter Setup Link Option On Utilities Screen", Intlogval);
