					RMA_Navigation_Utility.RMA_ObjectJavaScriptClick(RMA_Selenium_POM_DiaryUtilities.RMAApp_DiaryUtilities_Chk_TransferOpenDiary(driver), "Transfer Open Diary CheckBox On Transfer Diary Utilities", Intlogval);
