	//FunctionName 		: RMA_PrintedCheckCloneUtility
	//Description  		: Enables The User To Clone Printed Check
	//Input Parameter 	: PDFPassword of Type String, PDFVerificationText Of Type Object Array, logval Of Type Integer
	//Revision			: 0.0-DebasmitaPati-06-03-2020
	// ====================================================================================================================================================================================================================================================================================================
	public static void RMA_PrintedCheckCloneUtility(String PDFPassword,Object[] PDFVerificationText, int logval) throws Exception, Error
	{
		try {
			if (logval == 0)
			{
				parentlogger.log(LogStatus.INFO, "Clone Of Printed Check");
			}else{	
				logger = reports.startTest("<span id='CloneOfPrintedCheck'>Successful Cloning Of Printed Check</span>", "Clone Of Printed Check");
			}
			if(!(RMA_GenericUsages_Utility.RMA_GetCurrentFrameID()).equals(CurrentFrameID)){
				RMA_GenericUsages_Utility.RMA_DynamicFrameSwitching(CurrentFrameID);
				RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(logval);
				RMA_GenericUsages_Utility.RMA_StaticWait(5, logval, "Wait Is Added As Frame Is Getting Loaded");
			}		

			if((RMA_Selenium_POM_PaymentsCollections.RMAApp_Transaction_Img_ClonePrintedCheck(driver))!=null){
				RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_PaymentsCollections.RMAApp_Transaction_Img_ClonePrintedCheck(driver), "Clone Image Of Printed Check On Transaction Page", logval);		
			}		
			else{
				RMA_Functionality_Utility.RMA_ListOptionClick_Utility("PaymentCollection-CloneImageofPrintedCheck", logval);					
			}
			RMA_GenericUsages_Utility.RMA_DynamicFrameSwitching(RMA_Selenium_POM_VerificationMessage.ExternalAppFrame);			
			RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(logval);
			RMA_GenericUsages_Utility.RMA_StaticWait(8, logval, "Wait Is Added As Checks And EOB Reports Popup Needs To Get Loaded");
			RMA_Verification_Utility.RMA_ElementExist_Utility(RMA_Selenium_POM_Home.RMAApp_Generic_RMAAppText(driver,RMA_Selenium_POM_VerificationMessage.ChecksAndEOBReportsMsgForClonePrintCheck), "Checks And EOB Reports Message On Checks And EOB Reports Popup", logval);
			//Verification Of Checks And EOB Reports Message Is Done Here

			RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_Home.RMAApp_Generic_RMAAppText(driver, "CHECKS"),"Checks Link", logval);
			RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(logval);
			RMA_GenericUsages_Utility.RMA_StaticWait(5, logval, "Wait Is Added As Print Checks PDF Is Loaded");
			RMA_Verification_Utility.PDFReaderContentVerification_Utility(PDFPassword, PDFVerificationText, "Yes", logval);

			RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_PaymentsCollections.RMAApp_PaymentChecksAndEOBReports_Btn_Ok(driver), "Ok(Tick Mark Image Button) Button On Checks And EOB Reports Popup", logval);
			RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(logval);
			RMA_GenericUsages_Utility.RMA_StaticWait(5, logval, "Wait Is Added As Payment Page Needs To Get Loaded");

			if (logval == 0)
			{
				parentlogger.log(LogStatus.INFO, parentlogger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver, "Successful Cloning Of Printed Check", "Clone Of Printed Check Is Done Successfully")));
			}else{	
				logger.log(LogStatus.INFO, logger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver, "Successful Cloning Of Printed Check", "Clone Of Printed Check Is Done Successfully")));
			}
		}
		catch (Exception|Error e) {

			if (logval == 0)
			{
				parentlogger.log(LogStatus.INFO, logger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver, "Cloning Of Printed Check Fail", "Clone Of Printed Check Is UnSuccessful")));
			}else{	
				logger.log(LogStatus.INFO, logger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver, "Cloning Of Printed Check Fail", "Clone Of Printed Check Is UnSuccessful")));
			}
			throw (e);
		}		
	}	

