	//FunctionName          : RMA_PrintPreviewUtility_01
	//Description           : Enables The User To Do Print Preview Of Payments On Supervisory Approval Grid
	//Input Parameter       : ControlDesc Of Type String, logval Of Type Integer
	//Revision              : 0.0-VartikaSood-04-20-2021
	//====================================================================================================================================================================================================================================================================================================
	public static void RMA_PrintPreviewUtility_01(String ControlDesc,String PDFPassword,Object[] PDFVerificationText,int logval) throws Exception, Error
	{
		try {
			if (logval == 0)
			{
				parentlogger.log(LogStatus.INFO, "User Does Print Preview Of Checks");
			}else{      
				logger = reports.startTest("<span id='PrintPreview'>Successful Print Preview</span>", "User Does Print Preview Of Checks");
			}

			/*CurrentFrameID=null;
                    //ReturnValue = null;
                  CurrentFrameID=RMA_GenericUsages_Utility.RMA_GetCurrentFrameID();
                    //Global Variable Initialization Is Completed Here
			 */
			if(ControlDesc == "Payments")
			{
				RMA_GenericUsages_Utility.RMA_DynamicFrameSwitching(RMA_Selenium_POM_VerificationMessage.PrintSingleChecks);
				RMA_GenericUsages_Utility.RMA_StaticWait(6, logval, "Wait Is Added As Frame Is Getting Loaded");
				RMA_Verification_Utility.RMA_VerifyUXSpinnerDisappered(logval);
				RMA_Navigation_Utility.RMA_ObjectJavaScriptClick(RMA_Selenium_POM_SupervisoryApproval.RMAApp_SupervisoryApproval_PrintPriview(driver),"Print Preview Button On Print Preview Page", logval);
				RMA_GenericUsages_Utility.RMA_StaticWait(5, logval, "Wait Is Added As Checks And EOB Reports Popup Needs To Get Loaded");
				RMA_Verification_Utility.RMA_PartialTextVerification(RMA_Selenium_POM_VerificationMessage.ChecksAndEOBReportsMsg, RMA_Selenium_POM_PaymentsCollections.RMAApp_PrintSingleCheck_Txt_ChecksAndEOBReportsMsg(driver).getText(), "Checks And EOB Reports Message On Checks And EOB Reports Popup", logval);
				//Verification Of Checks And EOB Reports Message Is Done Here
				RMA_Navigation_Utility.RMA_ObjectJavaScriptClick(RMA_Selenium_POM_Home.RMAApp_Generic_RMAAppText(driver, "CHECKS"),"Checks Link On Print Preview Page", logval);
				//RMA_Verification_Utility.PDFReaderContentVerification_Utility(PDFPassword, PDFVerificationText, logval);

				//===================================================Steps To Verify Contents Of The PDF Is Started 
				

				Screen screen=new Screen();// Declare a Sikuli Screen 
				String FileName;
				FileName="TestFile"+RMA_GenericUsages_Utility.RMA_CurrentDateInfo_Utility("MMddyyyy")+RMA_GenericUsages_Utility.RMA_CurrentDateInfo_Utility("HHmmss");// Create Unique File Name
				PDFTextStripper pdfStripper = null;   
				PDDocument pdDoc = null;
				COSDocument cosDoc = null;
				String parsedText = null;
				Robot robot=new Robot();
				String PhysicalPath=System.getProperty("user.dir")+"\\RMATestInputFile\\DownloadTestFile\\"+FileName;// File Location to Store the File

				RMA_GenericUsages_Utility.RMA_StaticWait(5, logval, "Wait Is Added As PDF Authentication Popup Is Getting Opened");
				RMA_Input_Utility.RMA_ActionSetValue_Utility("Password TextBox on PDF Authentication Popup",PDFPassword,  logval);
				screen.type(PDFPassword);     // Enter The password
				if(logval == 0)
				{
					parentlogger.log(LogStatus.INFO, "Provided Text :" +" " +color.RMA_ChangeColor_Utility(PDFPassword, 3) +" " +"Is Set Successfully In Pattern:" +" " + color.RMA_ChangeColor_Utility("Password Textbox On PDF Authentication Popup" , 4));
				}
				else
				{
					logger.log(LogStatus.INFO, "Provided Text :" +" " +color.RMA_ChangeColor_Utility(PDFPassword, 3) +" " +"Is Set Successfully In Pattern:" +" " + color.RMA_ChangeColor_Utility("Password Textbox On PDF Authentication Popup" , 4));
				}
				RMA_GenericUsages_Utility.RMA_StaticWait(2, logval, "Wait Is Added As Typed Password On TextBox Successfully");

				robot.keyPress(KeyEvent.VK_ENTER);
				robot.keyRelease(KeyEvent.VK_ENTER);

				RMA_GenericUsages_Utility.RMA_StaticWait(5, logval, "Wait Is Added As File Is Getting Downloaded");
				RMA_Navigation_Utility.RMA_SikuliObjectMouseMove(RMA_Selenium_POM_Sikuli_Images.RMAApp_ChromeDownload_Img_MoveToVisibleDownloadButton(), "Move to View Download Icon On Chrome Browser", logval);// To Mouse Move On Top Of The PDF Browser to See the DownLoad Button
				RMA_Navigation_Utility.RMA_SikuliObjectClick(RMA_Selenium_POM_Sikuli_Images.RMAApp_ChromeDownload_Img_DownloadIcon(), "Download PDF File Icon on PDF Browser", logval);// Click On Download Button
				Thread.sleep(3000);
				RMA_GenericUsages_Utility.RMA_Upload_Save_File(PhysicalPath); // Give the File Location on Save As Popop
				Thread.sleep(5000);
				// Closs PDF after Save 
				robot.keyPress(KeyEvent.VK_CONTROL);
				robot.keyPress(KeyEvent.VK_F4);
				robot.keyRelease(KeyEvent.VK_F4);
				robot.keyRelease(KeyEvent.VK_CONTROL);
				//PDF Reader Through PDFBox Started Here

				File file = new File(PhysicalPath+".pdf");
				PDFParser parser = new PDFParser(new FileInputStream(file));// Parser to Parse Input File
				parser.parse(); //Parse Input File
				cosDoc = parser.getDocument();
				pdfStripper = new PDFTextStripper(); // PDFTextStripper to text extraction
				pdfStripper.setStartPage(1);
				pdfStripper.setEndPage(1);
				pdDoc = new PDDocument(cosDoc);

				//If PDF Password Protected
				boolean isOriginalDocEncrypted = pdDoc.isEncrypted();
				if (isOriginalDocEncrypted) {
					pdDoc.openProtection(new StandardDecryptionMaterial(PDFPassword));
				}

				parsedText = pdfStripper.getText(pdDoc);
				//PDF Reader Through PDFBox Ended Here

				//PDF Verification Started Here
				for(int i=0;i<PDFVerificationText.length;i++){
					RMA_Verification_Utility.RMA_PartialTextVerification(PDFVerificationText[i].toString(),parsedText,PDFVerificationText[i].toString()+" Value Verification", logval); 
				}

				//===================================================Steps To Verify Contents Of The PDF Is Completed 
				

				RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_PaymentsCollections.RMAApp_PaymentChecksAndEOBReports_Btn_Ok(driver), "Ok(Tick Mark Image Button) Button On Checks And EOB Reports Popup", logval);
				Thread.sleep(5000);
				RMA_Navigation_Utility.RMA_ObjectClick(RMA_Selenium_POM_PaymentsCollections.RMAApp_Payment_Btn_PrintPreviewCancel(driver),"Cancel Image On Payment Page", logval);
			}
			if (logval == 0)
			{
				parentlogger.log(LogStatus.INFO, parentlogger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver, "Successful Print Preview Of Check", "Print Preview Of Check Is Done")));
			}else{      
				logger.log(LogStatus.INFO, logger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver,  "Successful Print Preview Of Check", "Print Preview Of Check Is Done")));
			}     

		}

		catch (Exception|Error e) {
			if (logval == 0)
			{
				parentlogger.log(LogStatus.INFO, logger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver, "Print Preview Of Check Fail", "Print Preview Of Check Is Not Done")));
			}else{      
				logger.log(LogStatus.INFO, logger.addScreenCapture(RMA_ScreenCapture_Utility.RMA_ScreenShotCapture_Utility(driver, "Print Preview Of Check Fail", "Print Preview Of Check Is Not Done")));
			}

			throw (e);
		}
	}

